mapper.select:
select create_time,name,url,brand,code,sale_price,`status`,id 
from sku where 1=1 
 and seller_id =#{sellerId}
 and status =#{status}
 and  create_time between  #{beginTime} and #{endTime}

     and id in 
     
          #{idItem}
             
  
  
     and name like #{name} or code like #{name}

select
from sku where id in
#{item}
